       .GLOB L6575, LTSWN, CPDFLG       .GLOB L5DC5, FDSTT, FDNXT, FDEND, L64FD, L635E, L636E, L6023, L62FD, L6020, L5F53       .GLOB L62FC, L6036, L60BE, MWTXT, MVTTLP, MVTXTW, L60DD, SREDA, RECD10, CRLVL, L634C, BLKSM       .GLOB L6555, L64BC, L5DC4, L5FA1, L5DC3, L653D       .GLOB MVMNBT, MVTX2W, LTSWCL, L6015, MVBIND, L5FAC, L5DD8       .GLOB L5EC4, L5ED2, L5E9E, L5EB1, L5EA6, L5EB9, L5F4F, L5EE5, L5EF4       .GLOB L5E53, L5DC7, L5DCB, L5DCD, L5DE9, L5DC9, L5DCC, L5DCE, L64EE, L5E7D, L5E94       .GLOB MRKFLS, SPSND, RECD4       .GLOB ADMWN, ADOVW, ADDSR, ADDDS, ADOVF, ADERW, ADER1, ADCLW       .GLOB CLMWN, CLSHD, CLCLR, CLERW, BLNKWN; старт програмыL6575                        ; раскладываем цвета из UnCo по окнам       LD   HL,CLLST       LD   DE,CLMWN       LD   BC,#0905       EX   AF,AF'       LD   A,(CLSHD)       EX   AF,AF'1$       LD   A,(DE)       PUSH DE       LD   E,(HL)       INC  HL       LD   D,(HL)       INC  HL       LD   (DE),A       XOR  A       OR   C       JR   Z,2$       INC  DE       EX   AF,AF'       LD   (DE),A       EX   AF,AF'       DEC  C2$       POP  DE       INC  DE       DJNZ 1$       LD   A,(CLCLR)       LD   (ADCLW),A       LD   A,(CLERW)       LD   (ADER1),A       LD   A,(L5DC5)     ; макс. файлов за один заход       LD   HL,0       LD   DE,#0020       LD   B,AL6582  ADD  HL,DE       DJNZ L6582       EX   DE,HL       LD   HL,L6575       LD   (FDSTT),HL       LD   (FDNXT),HL       ADD  HL,DE       LD   (L64FD),HL   ; HL=#6975       LD   (FDEND),HL       LD   (SPSND),SP   ; сохраняем SP       CALL L67A6        ; обработка параметров командной строки       RET  C       LD   C,#87       RST  #10       LD   (MRKFLS),A    ; A-число отмеченных файлов       EXX       LD   A,H       LD   (L636E),A    ; H-максимальный номер отмеченного файла       DEC  BC       LD   A,(BC)       ; номер панели 0-левая, 1-правая       INC  BC       OR   A       JR   NZ,L65A3       INC  BCL65A3  LD   A,(BC)       ; (BC)-номер канала соответствующей панели       LD   C,#16       RST  #10          ; по номеру канала получаем данные канала       RET  C       EXX       LD   (L6023),HL   ; HL-адрес тела канала       LD   A,(HL)       LD   (L62FD),A    ; номер канала       INC  HL       LD   DE,L6020       LDI       LDI       LD   A,(HL)       LD   C,#16       RST  #10       RET  C       EXX       LD   DE,L5F53       LD   BC,#0037       LDIR             ; копируем текущий путь       LD   C,#33       RST  #10       EXX       LD   (L62FC),A       LD   (L6036),HL  ; номер 0-го блока текущего каталога       LD   A,(L60BE)       RRCA       JR   NC,COPCNT    ; смотрим - это copy или move       LD   HL,MWTXT+47       LD   (MWTXDS),HL                         ; Меняем заголовок окна       LD   HL,MOVTTL       LD   BC,MOVTLN       LD   DE,MVTTLP       LDIR                         ; Меняем надпись в окне       LD   HL,MOVTXT       LD   BC,MVTXLN       LD   DE,MVTXTW       LDIR                         ; Добиваем пробелами надпись       LD   H,D       LD   L,E       DEC  HL       LD   BC,#0004       LDIRCOPCNT       LD   A,(MRKFLS)       OR   A           ; A-число отмеченных файлов в тек. панели       JP   NZ,FLMRKD   ; файлы отмечены-переходим       LD   C,#8A       RST  #10       LD   (L60DD),A   ; получаем номер единственного файла       LD   DE,(L60DD)       LD   C,#27       RST  #10         ; HL'-дескриптор того файла       RET  C       EXX       PUSH HL       LD   BC,#000B       ADD  HL,BC       BIT  5,(HL); если каталог, выходим       POP  HL       JP   Z,COPC01       XOR  A       LD   (CPDFLG),A       LD   IX,SREDA       LD   C,#36       RST  #10                          ; каталог не пуст, требуется проверка на флаг и рекурсивное удаление       LD   A,(L60DD)       LD   C,#26       RST  #10       RET  C       EXXCRECD11       PUSH HL       DEC  HL       LD   A,(HL)       ; пол