HL       JR   COPC02COPC01       PUSH HL       CALL L634C       ; подсчёт блоков при копировании       LD   (BLKSM),HL  ; единственного файла       LD   A,#01       LD   (CPDFLG),A  ;COPC02       EXX       CALL L635E       ; получаем в HL маску отметки текущего файла       DEC  HL          ; переходим на номер отметки       SET  0,(HL)       LD   A,#1       LD   (MRKFLS),A   ; число отмеченных файлов                        ; вывод написи "filename.ext" в       LD   HL,(MWTXDS)       LD   (HL),#22    ; кавычку открываем       INC  HL       POP  DE       EX   DE,HL       LD   C,#4F       RST  #10       EXX       EX   DE,HL       LD   (HL),#22    ; кавычку закрываем       INC  HL       LD   (HL),#20       INC  HL       LD   (HL),"в"       EXX       JR   L65F8                        ; вывод надписи в главное окно "xxx файл(а,ов) в"FLMRKD       LD   DE,(MRKFLS)       LD   HL,(MWTXDS)       PUSH HL       XOR  A       LD   A,3       LD   BC,#0A7C       RST  #10       POP  HL       LD   BC,#0004       ADD  HL,BC       EX   DE,HL       LD   HL,MLSTXT       LD   BC,MLSTLN       LDIR       LD   A,(L636E)    ; количество файлов в каталоге       LD   B,AFLMR01       LD   E,B       LD   C,#86       RST  #10       EXX       DEC  HL       LD   A,(HL)       EXX       OR   A       JR   Z,FLMR02       LD   C,#27       RST  #10       RET  C       EXX       CALL L634C       LD   DE,(BLKSM)       ADD  HL,DE       LD   (BLKSM),HLFLMR02       DJNZ FLMR01       LD   A,#02       LD   (CPDFLG),AL65F8;       LD   A,#1;       LD   (L6161),A;       LD   (L6094),A;       LD   (L639E),A; Уменьшение дискового кэша       CALL L6555       ; уменьшаем кэш до #0C блоков       LD   C,#10       RST  #10         ; получаем в HL адрес вектора конфигурации ядра       EXX       PUSH HL       POP  IX       LD   L,(IX+#5)       LD   H,(IX+#6)   ; HL - адрес начала электронного диска       LD   DE,(L64FD)  ; адрес начала буфера для копирования       OR   A       SBC  HL,DE       ; вычисляем количество свободной памяти (буфер для копирования)L663F  LD   A,#23       RET  C       LD   A,H         ; A-количество блоков       LD   (L64BC),A       AND  A       SCF       JR   Z,L663F       CALL L6825       ; Проверка расширенной памяти       JR   NZ,L6663    ; NZ - нет расширенной памяти       LD   A,(L64BC)       LD   E,A       LD   D,#00       LD   HL,(BLKSM)       EX   DE,HL       OR   A       SBC  HL,DE       JR   C,L6664L6663       LD   A,#01       LD   (L5DC4),AL6664       CALL L6770        ; процедура подготовки текста меню настроекL6665  CALL L5FA1        ; подпрограмма запроса пути куда копировать       RET  C       JR   Z,L6679      ; переход, если выход по Enter       CP   #10       JP   Z,RECD4      ; нажат SS/A - выходим       LD   IX,MNUWIN       LD   A,#01       LD   C,#61       RST  #10       LD   C,#91       RST  #10          ; нажат SS/SP - работаем с меню       LD   A,(IX+#04)       PUSH AF       LD   A,(CLCLR)       LD   (IX+#04),A       INC  A       LD   C,#61       RST  #10       POP  AF       LD   (IX+#04),A       JR   L6665L6679       ; вставка обратного слэша в конец пути, если нет       LD   HL,L5F53       LD   BC,66       LD   A," "       CPIR       DEC  HL       DEC  HL       LD   A,"\"       CP   (HL)       JR   Z,1$       INC  HL       LD   (HL),A1$       CALL L6899        ; проверяем копирование на тот же диск       LD   A,(L5DC3)       AND  #8       CALL NZ,L653D     ; диски разные - открываем устройство и каталог назначения       RET  C       JR   NZ,L6665       LD   DE,MVMNBT       LD   H,D       LD   L,E       DEC  HL       LD   BC,#0008       LDIR       LD   HL,FRMTXT       LD   DE,MVTXTW       LD   BC,FRTXLN       LDIR       LD   H,D       LD   L,E       INC  DE       LD   BC,30       LD   (HL),#20       LDIR       LD   HL,TOTXT       LD   DE,MVTX2W       LD   B