; флаг копирования директорииCPDFLG DEFB #00         ; 0-каталог, 1-файл, 2-отмеченные файлыL673B  LD   HL,L5DC4       LD   A,(HL)       CP   #5       LD   A,#B       JR   Z,L674B       LD   A,#5       JR   C,L674B       LD   A,#1L674B  LD   (HL),A       CALL L6825L674F  JR   Z,L675E       JR   L673BL6753  LD   A,#2       JR   L6759L6757  LD   A,#1L6759  LD   HL,L5DC3       XOR  (HL)       LD   (HL),AL675E  XOR  A       INC  A       RETL6761       LD   A,#8       JR   L6759L6765       OR   #FF       RET; 0-й бит в B=1 - A="Y", B=0 - A="N"L6768  RRC  B       LD   A,#20       RET  NC       LD   A,#FB       RET; подпрограмма меню инициализации символьного массиваL6770  LD   A,(L5DC3)       LD   B,A       ; в B помещаем байт конфигурации       CALL L6768       LD   (OVRWRA),A       CALL L6768       LD   (OVWRAA),A       RRC  B       CALL L6768       XOR  #DB        ; меняем галку на пробел, или обратно       LD   (CHDSAS),A       LD   A,(L5DC4)       LD   DE,BUFMAA       CP   #5       LD   HL,BUFNOR       JR   C,L679F       LD   HL,BUF16K       JR   Z,L679F       LD   HL,BUF80KL679F       LD   BC,#0003       PUSH HL       LDIR           ; копируем строку +16 kb/+80 kb       LD   HL,BUFNOR       XOR  A       XOR  H       XOR  L       POP  HL       XOR  H       XOR  L       JR   NZ,BFINMW       LD   HL,MVBIND - 1 ; если в HL адрес BUFNOR, то заменяем его на MVBIND-1, там будет "═"                          ; копируем его 4 разаBFINMW       LD   DE,MVBIND       LD   BC,#0004       LDIR           ; индикация буфера на главном окне       XOR  A       RETL67A6  LD   C,#41       RST  #10       RET  C       LD   C,#43        ; получаем в A ключ командной строки (/c,/m, т.д.)       RST  #10       RET  C       LD   C,#41       JP   NZ,#0010     ; ключа нет после восстановления возврат       OR   A       JR   Z,L67A6      ; обнаружен путь\файл       RES  5,A          ; анализируем ключи       CP