        ORG 24000        LD A,(COLORS)        LD (MWIN+4),A        LD IX,MWIN        LD A,1        LD C,#61        RST #10        LD C,#62        LD DE,#0303        LD A,%00000000        RST #10        LD C,#62        LD DE,#0505        LD A,%00000000        RST #10        LD HL,TITLE        LD A,%11000000        LD E,1        LD C,#68        LD B,TITLEN        RST #10        LD C,#66        RST #10        LD A,(COLORS+1)        LD B,A        LD A,2        LD C,#64        RST #10        CALL WAITEXIT    XOR A        LD A,#F4        RETWAIT    LD C,#07        RST #10        RETMWIN    DEFB 5        DEFB 3        DEFB 16        DEFB 20        DEFB %00111000        DEFB %00000001        DEFB 8        DEFB 24TEXT    DEFM "Текст окна"        DEFB #0D        DEFM "Вторая строка"        DEFB #03TITLE   DEFM " Заголовок окна "TITLEN  EQU  $-TITLE;-------------------------------        DEFM "UnCo"        DEFB COLZCOLORS  DEFB %00111000        DEFB %00001111COLZ    EQU  $-COLORS