; 19.11.94  UnCo instead of COLOR TUNER; 22.4.92   11:27; 26.1.92    2:29; dname.as;=183ADRUN	EQU	25000	ORG	ADRUN	LD	C,$g_com	RST	16	EXX	LD	BC,#FFFB	ADD	HL,BC	LD	E,(HL)	INC	HL	LD	D,(HL)CMP	LD	A,(DE)	INC	DE	CP	13	JR	Z,GO_ON	CP	#20	JR	Z,CMP	LD	(DNCSR+1),A	EX	DE,HL	LD	A,(HL)	CP	":"	DEC	HL	JR	NZ,D_CPIR	LD	A,(HL)	LD	C,$dvtrn	RST	16	RET	NZ	LD	B,A	LD	C,$swblk	RST	16	RET	C	INC	HL	INC	HLD_CPIR	PUSH	HL	LD	A,13	LD	BC,9	CPIR	LD	BC,8	POP	DE	JR	NZ,GO_SZ	SBC	HL,DE	LD	C,L	LD	B,H	DEC	BC	LD	A,B	OR	C	JR	Z,GO_ONGO_SZ	EX	DE,HL	LD	DE,DNAME	LDIRGO_ON	LD	C,$g_cur	RST	16	EXX	LD	A,B	LD	BC,#0204	AND	A	JR	Z,RNMP	LD	BC,#1219RNMP	LD	IX,`DAY	LD	(IX),B	LD	(IX+6),C	LD	DE,0	LD	C,$quard	RST	16	RET	C	EXX	INC	HL	INC	HL	LD	(TMPHL),HL	LD	(TMPDE),DEDNCSR	LD	A,0	OR	A	JR	NZ,SAVE	CALL	DNPUTRESTA	LD	IX,`DAY	LD	HL,(COLOR)	LD	(`DAY+4),HL	LD	A,2	LD	C,$wt	RST	16	LD	C,$adrwt	RST	16	CALL	Dname	JR	NZ,EXITSAVE	LD	DE,(TMPHL)	LD	HL,DNAME	LD	B,8DS_CMP	LD	A,(DE)	CP	(HL)	JR	NZ,SAV_1	INC	HL	INC	DE	DJNZ	DS_CMP	JR	EXITSAV_1	LD	DE,(TMPHL)	LD	HL,DNAME	LD	BC,8	LDIR	LD	DE,(TMPDE)	LD	C,$modo	RST	16	RET	C	LD	C,$flush	RST	16	RET	CEXIT	XOR	A       LD   A,#F8	RET;------------------------	DEFM	|UnCo|	DEFB	COLSZCOLOR	DEFB	7,1COLSZ	EQU	$-COLOR;************************`DAY	DEFB	2,6,3,12,15,1,2,14	DEFM	|Label:|AFNAME	DEFM	|        |	DEFB	3*************************Dname	LD	HL,DNAME	LD	DE,AFNAME	LD	BC,8	LDIRRNCON	LD	HL,#106	LD	C,$wtpos	RST	16	LD	HL,AFNAME	LD	A,8	LD	DE,#0B00	LD	BC,#100+$smbgt	RST	16	RET	NZ	AND	A	JR	Z,RNCONDNPUT	LD	BC,8	LD	DE,DNAME	LDIR	XOR	A	RET;-----------------------DNAME	DEFM	|        |F_DAY	EQU	ADRUN-100TMPHL	EQU	F_DAY+2TMPDE	EQU	F_DAY+4;**;**